import { useState } from "react";
import "./App.css";
import SingleCard from "./components/SingleCard";

const cardImages = [
  { src: "/img/helmet-1.png" },
  { src: "/img/potion-1.png" },
  { src: "/img/ring-1.png" },
  { src: "/img/scroll-1.png" },
  { src: "/img/shield-1.png" },
  { src: "/img/sword-1.png" },
];

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);

  // need states to store user choices
  const [choiceOne, setChoiceOne] = useState(null)
  const [choiceTwo, setChoiceTwo] = useState(null)

  // called in a new game
  // shuffle cards
  const shuffleCards = () => {
    const shuffledDeck = [...cardImages, ...cardImages]
      // suffle cards
      .sort(() => Math.random() - 0.5)
      // creates a new object where each card also has an id
      .map((card) => ({ ...card, id: Math.random() }));

    // update the state
    setCards(shuffledDeck);
    setTurns(0);
  };


  // handle choice
    const handleChoice = (card) =>{
      if (choiceOne === null){
        setChoiceOne(card)
      }

      else {
        setChoiceTwo(card)
        compareCards();

      }

      console.log(choiceOne, choiceTwo,);
    }

    // compare cards

    const compareCards = (e) => {
        choiceOne.src === e.target.src ? console.log("Match") : console.log("No match");;
    }





  return (
    <div className="App">
      <h1>Magic Match</h1>
      <button onClick={shuffleCards}>New Game</button>

      <div className="card-grid">
        {cards.map((card) => (
          // pass down a prop
          <SingleCard key={card.id} card={card} handleChoice={handleChoice}/>
        ))}
      </div>
    </div>
  );
}

export default App;
