import { useEffect, useState } from "react";
import "./App.css";
import SingleCard from "./components/SingleCard";

const cardImages = [
  { src: "/img/helmet-1.png", matched: false },
  { src: "/img/potion-1.png", matched: false },
  { src: "/img/ring-1.png", matched: false },
  { src: "/img/scroll-1.png", matched: false },
  { src: "/img/shield-1.png", matched: false },
  { src: "/img/sword-1.png", matched: false },
];

function App() {
  const [cards, setCards] = useState([]);
  const [turns, setTurns] = useState(0);

  // need states to store user choices
  const [choiceOne, setChoiceOne] = useState(null);
  const [choiceTwo, setChoiceTwo] = useState(null);

  const [disabled, setDisabled] = useState(false)

  // called in a new game
  // shuffle cards
  const shuffleCards = () => {
    const shuffledDeck = [...cardImages, ...cardImages]
      // suffle cards
      .sort(() => Math.random() - 0.5)
      // creates a new object where each card also has an id
      .map((card) => ({ ...card, id: Math.random() }));

    // update the state
    setCards(shuffledDeck);
    setTurns(0);
    setChoiceTwo(null);
    setChoiceOne(null);
  };

  // handle choice
  const handleChoice = (card) => {
    if (choiceOne === null) {
      setChoiceOne(card);
    } else {
      setChoiceTwo(card);
    }
  };

  // compare cards, useEffect fires everytime choiceTwo is updated
  useEffect(() => {
    if (choiceTwo) {
    // disables card while doing comparison
    setDisabled(true)

      // if match, change flag to matched
      if (choiceOne.src === choiceTwo.src && choiceOne.id != choiceTwo.id) {
        setCards((prevCards) => {
          return prevCards.map((card) => {
            if (card.src === choiceOne.src) {
              return { ...card, matched: true };
            } else {
              return { ...card };
            }
          });
        });
      }
      // delay the reset
      setTimeout(() => resetTurn(), 1000);
    }
  }, [choiceTwo]);
  console.log(cards);


  // reset choices
  const resetTurn = () => {
    setChoiceTwo(null);
    setChoiceOne(null);
    setTurns((prevTurns) => prevTurns + 1);
    setDisabled(false)
  };

  // automatically start a new game
  useEffect(()=>{shuffleCards()},[])

  return (
    <div className="App">
      <h1>Magic Match</h1>
      <button onClick={shuffleCards}>New Game</button>
      <p>Turns: {turns}</p>

      <div className="card-grid">
        {cards.map((card) => (
          // pass down a prop

          <SingleCard
            key={card.id}
            card={card}
            handleChoice={handleChoice}
            flipped={
              card === choiceOne || card === choiceTwo || card.matched} // this card will be rendered as flipped if it is currently been selected
            disabled ={disabled}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
